<?php
$nucleos = array("A" => "T", "T" => "A", "C" => "G", "G" => "C");
$pattern = "";
/*
$output = "";
*/
$rpattern = reverse($pattern, $nucleos);
echo "Pattern = $pattern <br/>";
echo "Pattern* = $rpattern <br/>";
echo "<pre>";
print_r($nucleos);
echo "</pre>";
//echo "<br> Iguais = ";
//echo $rpattern == $output;
function reverse($pattern, $nucleos) {
    $rpattern = $pattern;
    for ($i = 0; $i < strlen($pattern); $i++ ) {
        $rpattern[strlen($pattern) - $i - 1] = $nucleos[$pattern[$i]];
    } 
    return $rpattern;
}
